import { INCREMENT, DECREMENT, ADD_TODO } from "../actions/todoAction";

const initialState = {
  todos: [
    {
      id: 1,
      title: "Buy Milk",
      isCompleted: true
    },
    {
      id: 2,
      title: "Buy Egg",
      isCompleted: false
    },
    {
      id: 3,
      title: "Buy T-Shirt",
      isCompleted: false
    }
  ]
};

function todoReducer(state = initialState, action) {
  switch (action.type) {
    case INCREMENT:
      return { value: state.value + 1 };
    case DECREMENT:
      return { value: state.value - 1 };
    case ADD_TODO:
      const newTodo = action.payload;
      return {
        ...state,
        todos: [...state.todos, newTodo]
      };
    default:
      return state;
  }
}

export default todoReducer;
